import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Drawer,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  IconButton,
  Typography,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
} from '@mui/material';
import {
  Add as AddIcon,
  Home as HomeIcon,
  Logout as LogoutIcon,
} from '@mui/icons-material';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';

const drawerWidth = 240;

function Dashboard() {
  const [servers, setServers] = useState([]);
  const [openDialog, setOpenDialog] = useState(false);
  const [newServerName, setNewServerName] = useState('');
  const navigate = useNavigate();
  const { logout } = useAuth();

  useEffect(() => {
    fetchServers();
  }, []);

  const fetchServers = async () => {
    try {
      const response = await axios.get('http://localhost:8000/servers');
      setServers(response.data);
    } catch (error) {
      console.error('Error fetching servers:', error);
    }
  };

  const handleCreateServer = async () => {
    try {
      await axios.post('http://localhost:8000/servers', {
        name: newServerName,
      });
      setOpenDialog(false);
      setNewServerName('');
      fetchServers();
    } catch (error) {
      console.error('Error creating server:', error);
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <Box sx={{ display: 'flex' }}>
      <Drawer
        variant="permanent"
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: drawerWidth,
            boxSizing: 'border-box',
            backgroundColor: '#2f3136',
          },
        }}
      >
        <Box sx={{ overflow: 'auto', mt: 2 }}>
          <List>
            <ListItem button onClick={() => navigate('/')}>
              <ListItemIcon>
                <HomeIcon sx={{ color: 'white' }} />
              </ListItemIcon>
              <ListItemText primary="Главная" />
            </ListItem>
          </List>
          <Typography variant="subtitle2" sx={{ px: 2, mt: 2, color: 'gray' }}>
            СЕРВЕРЫ
          </Typography>
          <List>
            {servers.map((server) => (
              <ListItem
                button
                key={server.id}
                onClick={() => navigate(`/servers/${server.id}`)}
              >
                <ListItemText primary={server.name} />
              </ListItem>
            ))}
            <ListItem button onClick={() => setOpenDialog(true)}>
              <ListItemIcon>
                <AddIcon sx={{ color: 'white' }} />
              </ListItemIcon>
              <ListItemText primary="Создать сервер" />
            </ListItem>
          </List>
          <List sx={{ mt: 'auto' }}>
            <ListItem button onClick={handleLogout}>
              <ListItemIcon>
                <LogoutIcon sx={{ color: 'white' }} />
              </ListItemIcon>
              <ListItemText primary="Выйти" />
            </ListItem>
          </List>
        </Box>
      </Drawer>
      <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
        <Typography variant="h4" sx={{ mb: 2 }}>
          Добро пожаловать!
        </Typography>
        <Typography variant="body1">
          Выберите сервер из списка слева или создайте новый.
        </Typography>
      </Box>

      <Dialog open={openDialog} onClose={() => setOpenDialog(false)}>
        <DialogTitle>Создать новый сервер</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Название сервера"
            fullWidth
            value={newServerName}
            onChange={(e) => setNewServerName(e.target.value)}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDialog(false)}>Отмена</Button>
          <Button onClick={handleCreateServer} variant="contained">
            Создать
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}

export default Dashboard;
