import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';
import config from '../config';

const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [token, setToken] = useState(localStorage.getItem('token'));
    const [loading, setLoading] = useState(true);

    // Configure axios defaults
    axios.defaults.baseURL = config.API_BASE_URL;
    axios.defaults.headers.common['Authorization'] = token ? `Bearer ${token}` : '';

    const checkAuth = async () => {
        if (!token) {
            setLoading(false);
            return;
        }

        try {
            const response = await axios.get('/users/me/');
            setUser(response.data);
        } catch (error) {
            console.error('Auth check error:', error);
            if (error.response?.status === 401) {
                // Token is invalid or expired, try to refresh
                try {
                    const refreshResponse = await axios.post('/token/refresh', {}, {
                        headers: { 'Authorization': `Bearer ${token}` }
                    });
                    const newToken = refreshResponse.data.access_token;
                    localStorage.setItem('token', newToken);
                    setToken(newToken);
                    axios.defaults.headers.common['Authorization'] = `Bearer ${newToken}`;
                    
                    // Retry getting user data with new token
                    const userResponse = await axios.get('/users/me/');
                    setUser(userResponse.data);
                } catch (refreshError) {
                    console.error('Token refresh failed:', refreshError);
                    logout();
                }
            } else {
                logout();
            }
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        checkAuth();
    }, [token]);

    const register = async (email, password, username) => {
        try {
            // Validate email format
            const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
            if (!emailRegex.test(email)) {
                throw new Error('Please enter a valid email address');
            }

            // Validate password format
            const passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[!@#$%^&*])[A-Za-z\d!@#$%^&*]{8,}$/;
            if (!passwordRegex.test(password)) {
                throw new Error('Password must be at least 8 characters long and contain uppercase, lowercase, number, and special character');
            }

            // First, try to register the user
            const response = await axios.post('/users/', {
                email,
                password,
                username
            }, {
                headers: {
                    'Content-Type': 'application/json'
                }
            });
            
            // After successful registration, try to log in
            try {
                await login(email, password);
                return true;
            } catch (loginError) {
                console.error('Auto-login after registration failed:', loginError);
                // Even if auto-login fails, registration was successful
                return true;
            }
        } catch (error) {
            console.error('Registration error:', error);
            if (error.response?.data?.detail) {
                // Handle specific error messages from the server
                const errorMessage = error.response.data.detail;
                if (errorMessage === 'Email already registered') {
                    throw new Error('This email is already registered. Please use a different email or try logging in.');
                } else if (errorMessage === 'Username already taken') {
                    throw new Error('This username is already taken. Please choose a different username.');
                } else {
                    throw new Error(errorMessage);
                }
            }
            throw error;
        }
    };

    const login = async (email, password) => {
        try {
            // Validate email format
            const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
            if (!emailRegex.test(email)) {
                throw new Error('Please enter a valid email address');
            }

            // Create URLSearchParams for the request
            const params = new URLSearchParams();
            params.append('username', email);
            params.append('password', password);

            console.log('Attempting login with:', { email });
            
            const response = await axios.post('/token', params, {
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded'
                }
            });
            
            console.log('Login response:', response.data);
            
            const newToken = response.data.access_token;
            if (!newToken) {
                throw new Error('No access token received from server');
            }
            
            localStorage.setItem('token', newToken);
            setToken(newToken);
            axios.defaults.headers.common['Authorization'] = `Bearer ${newToken}`;
            await checkAuth();
            return true;
        } catch (error) {
            console.error('Login error details:', {
                status: error.response?.status,
                statusText: error.response?.statusText,
                data: error.response?.data,
                message: error.message
            });
            
            if (error.response?.data?.detail) {
                throw new Error(error.response.data.detail);
            } else if (error.response?.status === 401) {
                throw new Error('Invalid email or password. Please check your credentials and try again.');
            } else if (error.response?.status === 422) {
                throw new Error('Invalid login data format. Please check your email and password.');
            } else {
                throw new Error(error.message || 'Login failed. Please try again.');
            }
        }
    };

    const logout = () => {
        localStorage.removeItem('token');
        setToken(null);
        setUser(null);
        delete axios.defaults.headers.common['Authorization'];
    };

    const value = {
        user,
        token,
        loading,
        login,
        logout,
        register,
        setToken,
        checkAuth
    };

    return (
        <AuthContext.Provider value={value}>
            {!loading && children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => {
    const context = useContext(AuthContext);
    if (!context) {
        throw new Error('useAuth must be used within an AuthProvider');
    }
    return context;
};
