import React, { useEffect, useRef, useState } from 'react';
import {
    Box,
    Button,
    Typography,
    IconButton,
    Paper,
    Avatar,
    List,
    ListItem,
    ListItemAvatar,
    ListItemText,
    Slider,
    Tooltip,
    Badge,
    Divider,
    Alert
} from '@mui/material';
import {
    Mic as MicIcon,
    MicOff as MicOffIcon,
    VolumeUp as VolumeUpIcon,
    VolumeOff as VolumeOffIcon,
    Headset as HeadsetIcon,
    HeadsetOff as HeadsetOffIcon,
    Settings as SettingsIcon,
    ScreenShare as ScreenShareIcon,
    Videocam as VideoIcon,
    VideocamOff as VideoOffIcon,
    MoreVert as MoreVertIcon
} from '@mui/icons-material';
import { useAuth } from '../contexts/AuthContext';
import config from '../config';

const VoiceChannel = ({ channelId }) => {
    const { token } = useAuth();
    const [isConnected, setIsConnected] = useState(false);
    const [isMuted, setIsMuted] = useState(false);
    const [isDeafened, setIsDeafened] = useState(false);
    const [isVideoEnabled, setIsVideoEnabled] = useState(false);
    const [isScreenSharing, setIsScreenSharing] = useState(false);
    const [participants, setParticipants] = useState([]);
    const [volume, setVolume] = useState(100);
    const [showSettings, setShowSettings] = useState(false);
    const [connectionStatus, setConnectionStatus] = useState('disconnected'); // 'disconnected', 'connecting', 'connected'
    const [error, setError] = useState('');
    
    const wsRef = useRef(null);
    const mediaStreamRef = useRef(null);
    const audioContextRef = useRef(null);
    const mediaRecorderRef = useRef(null);
    const videoStreamRef = useRef(null);
    const screenStreamRef = useRef(null);

    useEffect(() => {
        connect();
        return () => {
            disconnect();
        };
    }, [channelId]); // Reconnect when channel changes

    const connect = async () => {
        try {
            setConnectionStatus('connecting');
            setError('');

            // Get the WebSocket protocol based on the current protocol
            const wsProtocol = window.location.protocol === 'https:' ? 'wss:' : 'ws:';
            const wsUrl = `${wsProtocol}//${config.SERVER_IP}:${config.SERVER_PORT}/ws/voice/${channelId}?token=${token}`;
            
            console.log('Connecting to WebSocket:', wsUrl);
            wsRef.current = new WebSocket(wsUrl);

            wsRef.current.onopen = () => {
                console.log('WebSocket connected successfully');
                setConnectionStatus('connected');
                setIsConnected(true);
                startAudioStream();
                // Notify server about new participant
                wsRef.current.send(JSON.stringify({
                    type: 'join',
                    userId: localStorage.getItem('userId')
                }));
            };

            wsRef.current.onclose = (event) => {
                console.log('WebSocket closed:', event.code, event.reason);
                setConnectionStatus('disconnected');
                setIsConnected(false);
                stopAudioStream();
                stopVideoStream();
                stopScreenShare();
                
                // Try to reconnect if the connection was lost
                if (event.code !== 1000) {
                    setTimeout(() => {
                        console.log('Attempting to reconnect...');
                        connect();
                    }, 3000);
                }
            };

            wsRef.current.onerror = (error) => {
                console.error('WebSocket error:', error);
                setError('Failed to connect to voice channel. Please check your internet connection and try again.');
                setConnectionStatus('disconnected');
                disconnect();
            };

            wsRef.current.onmessage = async (event) => {
                if (event.data instanceof Blob) {
                    const audioData = await event.data.arrayBuffer();
                    playAudio(audioData);
                } else {
                    const data = JSON.parse(event.data);
                    handleWebSocketMessage(data);
                }
            };
        } catch (error) {
            console.error('Error connecting to voice channel:', error);
            setError('Failed to connect to voice channel. Please try again later.');
            setConnectionStatus('disconnected');
        }
    };

    const handleWebSocketMessage = (data) => {
        switch (data.type) {
            case 'participants':
                setParticipants(data.participants);
                break;
            case 'participant_joined':
                setParticipants(prev => [...prev, data.participant]);
                break;
            case 'participant_left':
                setParticipants(prev => prev.filter(p => p.id !== data.userId));
                break;
            default:
                console.log('Unknown message type:', data.type);
        }
    };

    const disconnect = () => {
        if (wsRef.current) {
            wsRef.current.close();
            wsRef.current = null;
        }
        stopAudioStream();
        stopVideoStream();
        stopScreenShare();
        setIsConnected(false);
    };

    const startAudioStream = async () => {
        try {
            const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
            mediaStreamRef.current = stream;

            // Initialize MediaRecorder
            mediaRecorderRef.current = new MediaRecorder(stream);
            mediaRecorderRef.current.ondataavailable = (event) => {
                if (event.data.size > 0 && wsRef.current?.readyState === WebSocket.OPEN) {
                    wsRef.current.send(event.data);
                }
            };

            // Start recording
            mediaRecorderRef.current.start(100); // Send data every 100ms
        } catch (error) {
            console.error('Error accessing microphone:', error);
        }
    };

    const stopAudioStream = () => {
        if (mediaRecorderRef.current) {
            mediaRecorderRef.current.stop();
            mediaRecorderRef.current = null;
        }
        if (mediaStreamRef.current) {
            mediaStreamRef.current.getTracks().forEach(track => track.stop());
            mediaStreamRef.current = null;
        }
    };

    const startVideoStream = async () => {
        try {
            // Request both video and audio to ensure we have the right permissions
            const stream = await navigator.mediaDevices.getUserMedia({ 
                video: {
                    width: { ideal: 1280 },
                    height: { ideal: 720 },
                    facingMode: 'user'
                },
                audio: true 
            });
            
            console.log('Video stream started:', stream.getVideoTracks()[0].label);
            videoStreamRef.current = stream;
            setIsVideoEnabled(true);
            
            // Send video stream to other participants
            if (wsRef.current?.readyState === WebSocket.OPEN) {
                wsRef.current.send(JSON.stringify({
                    type: 'video_start',
                    userId: localStorage.getItem('userId')
                }));
            }

            // Add video element to display the stream
            const videoElement = document.createElement('video');
            videoElement.srcObject = stream;
            videoElement.autoplay = true;
            videoElement.muted = true; // Mute to prevent feedback
            videoElement.style.width = '100%';
            videoElement.style.maxHeight = '200px';
            videoElement.style.objectFit = 'cover';
            videoElement.style.borderRadius = '8px';
            
            // Add video element to the UI
            const videoContainer = document.getElementById('video-container');
            if (videoContainer) {
                // Remove any existing video elements first
                while (videoContainer.firstChild) {
                    videoContainer.removeChild(videoContainer.firstChild);
                }
                videoContainer.appendChild(videoElement);
            }
        } catch (error) {
            console.error('Error accessing camera:', error);
            setError('Failed to access camera. Please make sure you have granted camera permissions.');
            setIsVideoEnabled(false);
        }
    };

    const stopVideoStream = () => {
        if (videoStreamRef.current) {
            videoStreamRef.current.getTracks().forEach(track => track.stop());
            videoStreamRef.current = null;
            setIsVideoEnabled(false);
            
            if (wsRef.current?.readyState === WebSocket.OPEN) {
                wsRef.current.send(JSON.stringify({
                    type: 'video_stop',
                    userId: localStorage.getItem('userId')
                }));
            }

            // Clear video container
            const videoContainer = document.getElementById('video-container');
            if (videoContainer) {
                // Remove any existing video elements first
                while (videoContainer.firstChild) {
                    videoContainer.removeChild(videoContainer.firstChild);
                }
            }
        }
    };

    const startScreenShare = async () => {
        try {
            const stream = await navigator.mediaDevices.getDisplayMedia({ video: true });
            screenStreamRef.current = stream;
            setIsScreenSharing(true);
            
            if (wsRef.current?.readyState === WebSocket.OPEN) {
                wsRef.current.send(JSON.stringify({
                    type: 'screen_share_start',
                    userId: localStorage.getItem('userId')
                }));
            }
        } catch (error) {
            console.error('Error sharing screen:', error);
        }
    };

    const stopScreenShare = () => {
        if (screenStreamRef.current) {
            screenStreamRef.current.getTracks().forEach(track => track.stop());
            screenStreamRef.current = null;
            setIsScreenSharing(false);
            
            if (wsRef.current?.readyState === WebSocket.OPEN) {
                wsRef.current.send(JSON.stringify({
                    type: 'screen_share_stop',
                    userId: localStorage.getItem('userId')
                }));
            }
        }
    };

    const playAudio = (audioData) => {
        if (!audioContextRef.current) {
            audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)();
        }

        audioContextRef.current.decodeAudioData(audioData, (buffer) => {
            const source = audioContextRef.current.createBufferSource();
            const gainNode = audioContextRef.current.createGain();
            gainNode.gain.value = volume / 100;
            
            source.buffer = buffer;
            source.connect(gainNode);
            gainNode.connect(audioContextRef.current.destination);
            source.start(0);
        });
    };

    const toggleMute = () => {
        if (mediaStreamRef.current) {
            const audioTrack = mediaStreamRef.current.getAudioTracks()[0];
            audioTrack.enabled = !audioTrack.enabled;
            setIsMuted(!isMuted);
        }
    };

    const toggleDeafen = () => {
        setIsDeafened(!isDeafened);
        if (audioContextRef.current) {
            audioContextRef.current.destination.volume = isDeafened ? volume / 100 : 0;
        }
    };

    const toggleVideo = () => {
        if (isVideoEnabled) {
            stopVideoStream();
        } else {
            startVideoStream();
        }
    };

    const toggleScreenShare = () => {
        if (isScreenSharing) {
            stopScreenShare();
        } else {
            startScreenShare();
        }
    };

    const handleVolumeChange = (event, newValue) => {
        setVolume(newValue);
        if (audioContextRef.current && !isDeafened) {
            audioContextRef.current.destination.volume = newValue / 100;
        }
    };

    return (
        <Box sx={{ 
            display: 'flex', 
            flexDirection: 'column',
            height: '100%',
            bgcolor: 'background.paper'
        }}>
            {/* Connection Status */}
            {error && (
                <Alert severity="error" sx={{ mb: 2 }}>
                    {error}
                </Alert>
            )}
            
            {connectionStatus === 'connecting' && (
                <Alert severity="info" sx={{ mb: 2 }}>
                    Connecting to voice channel...
                </Alert>
            )}

            {/* Video Container */}
            <Box 
                id="video-container" 
                sx={{ 
                    p: 2, 
                    display: 'flex', 
                    justifyContent: 'center',
                    bgcolor: 'background.default'
                }}
            >
                {!isVideoEnabled && (
                    <Typography variant="body2" color="text.secondary">
                        Camera is disabled
                    </Typography>
                )}
            </Box>

            {/* Participants List */}
            <List sx={{ flex: 1, overflow: 'auto' }}>
                {participants.map((participant) => (
                    <ListItem key={participant.id}>
                        <ListItemAvatar>
                            <Badge
                                overlap="circular"
                                anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
                                badgeContent={
                                    <Box sx={{ 
                                        width: 12, 
                                        height: 12, 
                                        borderRadius: '50%',
                                        bgcolor: participant.isMuted ? 'error.main' : 'success.main',
                                        border: '2px solid',
                                        borderColor: 'background.paper'
                                    }} />
                                }
                            >
                                <Avatar>{participant.username[0]}</Avatar>
                            </Badge>
                        </ListItemAvatar>
                        <ListItemText 
                            primary={participant.username}
                            secondary={
                                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                                    {participant.isMuted && <MicOffIcon fontSize="small" />}
                                    {participant.isDeafened && <HeadsetOffIcon fontSize="small" />}
                                    {participant.isVideoEnabled && <VideoIcon fontSize="small" />}
                                    {participant.isScreenSharing && <ScreenShareIcon fontSize="small" />}
                                </Box>
                            }
                        />
                    </ListItem>
                ))}
            </List>

            {/* Controls */}
            <Box sx={{ 
                p: 2, 
                borderTop: 1, 
                borderColor: 'divider',
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center'
            }}>
                <Box sx={{ display: 'flex', gap: 1 }}>
                    <Tooltip title={isMuted ? "Unmute" : "Mute"}>
                        <IconButton onClick={toggleMute} color={isMuted ? "error" : "default"}>
                            {isMuted ? <MicOffIcon /> : <MicIcon />}
                        </IconButton>
                    </Tooltip>
                    <Tooltip title={isDeafened ? "Undeafen" : "Deafen"}>
                        <IconButton onClick={toggleDeafen} color={isDeafened ? "error" : "default"}>
                            {isDeafened ? <HeadsetOffIcon /> : <HeadsetIcon />}
                        </IconButton>
                    </Tooltip>
                </Box>

                <Box sx={{ display: 'flex', gap: 1 }}>
                    <Tooltip title={isVideoEnabled ? "Disable Video" : "Enable Video"}>
                        <IconButton onClick={toggleVideo} color={isVideoEnabled ? "primary" : "default"}>
                            {isVideoEnabled ? <VideoIcon /> : <VideoOffIcon />}
                        </IconButton>
                    </Tooltip>
                    <Tooltip title={isScreenSharing ? "Stop Sharing" : "Share Screen"}>
                        <IconButton onClick={toggleScreenShare} color={isScreenSharing ? "primary" : "default"}>
                            <ScreenShareIcon />
                        </IconButton>
                    </Tooltip>
                </Box>

                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, minWidth: 200 }}>
                    <VolumeUpIcon />
                    <Slider
                        value={volume}
                        onChange={handleVolumeChange}
                        aria-labelledby="volume-slider"
                        min={0}
                        max={100}
                    />
                </Box>
            </Box>
        </Box>
    );
};

export default VoiceChannel;
