// Server configuration
const SERVER_IP = 'localhost';
const SERVER_PORT = window.location.port || '8000'; // Use current port or default to 8000

// API endpoints
const API_BASE_URL = `http://${SERVER_IP}:${SERVER_PORT}`;
const WS_BASE_URL = `ws://${SERVER_IP}:${SERVER_PORT}`;

export default {
    SERVER_IP,
    SERVER_PORT,
    API_BASE_URL,
    WS_BASE_URL
};
